import React, { useState } from "react";

const App = () => {

    let time = new Date().toLocaleTimeString();

    const [ctime, setTime] = useState(time);

    const UpdateTime = () =>{

        ctime = new Date().toLocaleTimeString();     
        setTime(ctime);    
       
        setInterval(UpdateTime,1000);
    
    };
    return (
        <>
        <h1> {UpdateTime} </h1>
       
        </>
    
    );

}
    
    export default App;
